import React, { useState } from 'react'
import logout from './assets/logout';
import './CheckerHome.css';
import Editor from './Editor';
import Highlighter from './Highlighter';

const CheckerHome = () => {
    const [input, setInput] = useState("");
    const [language, setLanguage] = useState(defaultLanguage);
    const [theme, setTheme] = useState(defaultTheme);
    return (
        <div className='body'>
            <h1>CHECKER HOME PAGE</h1>
            <hr className='line-top' />
            <div className='userdetails'>
                <h4 className='username'>Username</h4>
                <img className='logouticon' src={logout} alt='' />
            </div>
            <hr className='line-top2' />
            {/* <textarea className='codearea'></textarea> */}
            <Editor
                placeHolder="Type your code here..."
                onChange={(e) => setInput(e.target.value)}
            />
            <Highlighter language={language} theme={themes[theme]}>
                {input}
            </Highlighter>
        </div>
    )
}

export default CheckerHome
